import React, { Component } from 'react';

export default class CalculationsList extends Component {
    constructor(props) {
        super(props);

        this.onChangeOperand1=this.onChangeOperand1.bind(this);
        this.onChangeOperand2=this.onChangeOperand2.bind(this);
        this.onChangeOperator=this.onChangeOperator.bind(this);
        this.onSubmit=this.onSubmit.bind(this);

        this.state = {
            operand1: '',
            operand2: '',
            operator: '',
            calculations: []
        }
    }

    componentDidMount(){
        this.setState({
            calculations: ['test calculation'],
            operand1: 'test operand1',
            operand2:'test operand2',
            operator: 'test operator',
            result:'test result'
        })
    }

    onChangeOperand1(e) {
        this.setState({
            operand1: e.target.value
        });
    }

    onChangeOperand2(e) {
        this.setState({
            operand2: e.target.value
        });
    }
    onChangeOperator(e) {
        this.setState({
            operator: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const calculation = {
            operand1: this.state.operand1,
            operand2: this.state.operand2,
            operator: this.state.operator

        }
        console.log(calculation)
        window.location = '/';

    }

    render() {
        return (
            <div>
                <h3>Welcome to calculations page!!</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Operand1: </label>
                        </div>
                        <div><label>Operand2: </label></div>
                        
                        <div className="form-group">
                        <label>Operator: </label>
                        <select ref="userInput"
                        requiredclassName="form-control"
                        value={this.state.operator}
                        onChange={this.onChangeOperator}>
                            <option value="+">+</option>
                            <option value="-">+</option>
                            <option value="*">+</option>
                            <option value="/">+</option>
                        </select>
                        </div>
                    
                </form>
            </div>
        )
    }
}
