import React, { Component } from 'react';

export default class CalculationsList extends Component {
    constructor(props) {
        super(props);

        this.onChangeOperand1 = this.onChangeOperand1.bind(this);
        this.onChangeOperand2 = this.onChangeOperand2.bind(this);
        this.onChangeOperator = this.onChangeOperator.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            operand1: '',
            operand2: '',
            operator: '',
            calculations: []
        }
    }

    componentDidMount() {
        this.setState({
            calculations: ['test calculation'],
            operand1: 'Enter operand 1',
            operand2: 'Enter operand 2',
            operator: 'Select operator',
            result: 'test result'
        })
    }

    onChangeOperand1(e) {
        this.setState({
            operand1: e.target.value
        });
    }

    onChangeOperand2(e) {
        this.setState({
            operand2: e.target.value
        });
    }
    onChangeOperator(e) {
        this.setState({
            operator: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        const calculation = {
            operand1: this.state.operand1,
            operand2: this.state.operand2,
            operator: this.state.operator

        }
        console.log(calculation)
        window.location = '/';

    }

    render() {
        return (
            <div>
                <h3>Welcome to calculations page!!</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Operand1: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.operand1}
                            onChange={this.onChangeOperand1}
                        />
                    </div >

                    <div className="form-group">
                        <label>Operand2: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.operand2}
                            onChange={this.onChangeOperand2}
                        />
                    </div>

                    <div className="form-group">
                        <label>Operator: </label>
                        <select ref="userInput"
                            required
                            className="form-control"
                            value={this.state.operator}
                            onChange={this.onChangeOperator}>
                            <option value="+">+</option>
                            <option value="-">-</option>
                            <option value="*">*</option>
                            <option value="/">/</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="calculate" className="btn btn-primary" />
                    </div>
                </form>
                <table striped bordered hover size="sm">
                    <thead>
                        <th>Operand 1</th>
                        <th>Operand 2</th>
                        <th>Operator</th>
                        <th>Result</th>
                    </thead>

                    <tbody>
                        {
                            this.state.calculations.map(function (calc) {
                                return <tr>
                                    <td>{calc.operand1}</td>
                                    <td>{calc.operand2}</td>
                                    <td>{calc.operator}</td>
                                    <td>{calc.result}</td>
                                </tr>
                            })

                        }
                    </tbody>

                </table>
            </div>
        )
    }
}
